import React, { useState } from 'react';
import axios from 'axios';

const FlightSearch = () => {
  // State variables for form inputs
  const [userPreferences, setUserPreferences] = useState({
    departureCity: '',
    destinationCity: '',
    preferredAirline: '',
    weight: 0.5, // Default weight for user preferences
  });

  const [enterprisePreferences, setEnterprisePreferences] = useState({
    weight: 0.5, // Default weight for enterprise preferences
  });

  const [rankedFlights, setRankedFlights] = useState([]);

  // Example options (you can replace these with real data)
  const departureCities = ['New York', 'Los Angeles', 'Chicago', 'San Francisco'];
  const destinationCities = ['London', 'Paris', 'Tokyo', 'Sydney'];
  const airlines = ['United', 'Delta', 'American Airlines', 'Lufthansa'];

  // Handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserPreferences((prev) => ({ ...prev, [name]: value }));
  };

  // Fetch ranked flights from backend
  const fetchRankedFlights = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/rank_flights', {
        user_preferences: userPreferences,
        enterprise_preferences: enterprisePreferences,
      });
      setRankedFlights(response.data);
    } catch (error) {
      console.error('Error fetching ranked flights:', error);
    }
  };

  return (
    <div>
      <h2>Flight Search</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchRankedFlights();
        }}
      >
        <label>
          Departure City:
          <select name="departureCity" value={userPreferences.departureCity} onChange={handleChange}>
            <option value="">Select a city</option>
            {departureCities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </label>
        <br />

        <label>
          Destination City:
          <select name="destinationCity" value={userPreferences.destinationCity} onChange={handleChange}>
            <option value="">Select a city</option>
            {destinationCities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </label>
        <br />

        <label>
          Preferred Airline:
          <select name="preferredAirline" value={userPreferences.preferredAirline} onChange={handleChange}>
            <option value="">Select an airline</option>
            {airlines.map((airline, index) => (
              <option key={index} value={airline}>
                {airline}
              </option>
            ))}
          </select>
        </label>
        <br />

        <button type="submit">Search Flights</button>
      </form>

      {/* Display the ranked flights */}
      <div>
        <h3>Top 3 Ranked Flights</h3>
        {rankedFlights.length === 0 ? (
          <p>No flights found.</p>
        ) : (
          <ul>
            {rankedFlights.map((flight, index) => (
              <li key={index}>
                {flight.departureCity} to {flight.destinationCity} with {flight.preferredAirline} - Score: {flight.score}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default FlightSearch;
